const app = Vue.createApp({
  data(){
      return {
          activeTab: 'tabs-1',
          tabs: [
              {name:'tabs-1', text: '未着手'},
              {name:'tabs-2', text: '実施中'},
              {name:'tabs-3', text: '完了'},
          ],
      }
  },
})
const vm = app.mount("#app")


const access_time = new Date()
const min_time = 3 * 60 * 1000 //3分をミリ秒にしたもの

let countdown = setInterval(function(){
    const now = new Date()  //今の日時
    // const target = new Date("2022/9/11 1:00:00")   //最初にアクセスした時間
    const elapsed_time = now - access_time //カウントアップ
    const remainTime = min_time - elapsed_time  //差分を取る（ミリ秒で返ってくる


    //指定の日時を過ぎていたら処理をしない
    if(remainTime < 0) return false 

    //差分の日・時・分・秒を取得
    const difMin  = Math.floor(remainTime / 1000 / 60) % 60
    const difSec  = Math.floor(remainTime / 1000) % 60

    //残りの日時を上書き
    document.getElementById("countdown-min").textContent  = difMin
    document.getElementById("countdown-sec").textContent  = difSec

    //指定の日時になればカウントを止める
    if(remainTime < 0) clearInterval(countdown)

}, 1000)    //1秒間に1度処理
